, 'publish', 'closed', 'closed', '', 'contact-form-1', '', '', '2016-09-26 18:55:42', '2016-09-26 18:55:42', '', 0, 'http://localhost/sadaka/?post_type=wpcf7_contact_form&#038;p=27', 0, 'wpcf7_contact_form', '', 0),
